document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".carousel-wrapper");e.forEach((e,t)=>{const s=Array.from(e.children),n=document.querySelectorAll(`.carousel-dots[data-for='c${t}'] span`),o=new IntersectionObserver(e=>{e.forEach(e=>{if(e.isIntersecting){const t=s.indexOf(e.target);n.forEach(e=>e.classList.remove("active")),n[t]&&n[t].classList.add("active")}})},{root:e,threshold:.5});s.forEach(e=>o.observe(e))})})